question.answer:
public class X {

    // write your code here

    public static void main(String[] args) {
        // test your code here
        Turtle bob = new Turtle();
        shelf(bob);
        book(bob, 20, 16);
        book(bob, 40, 24);
        book(bob, 35, 12);
        book(bob, 15, 13);
        book(bob, 25, 10);
        book(bob, 13, 15);
        bob.backward(90);
    }

    public static void shelf(Turtle t) {
        for (int i = 0; i < 2; i++) {
            t.forward(100);
            t.left(90);
            t.forward(50);
            t.left(90);
        }
    }

    public static void book(Turtle t, double length, double width) {
        for (int i = 0; i < 2; i++) {
            t.forward(width);
            t.left(90);
            t.forward(length);
            t.left(90);
        }
        t.forward(width);
    }


}
