question.init:
public class X {

    // write your code here

    public static void main(String[] args) {
        // test your code here
    }

}

question.answer:
public class X {

    // write your code here
    public static int sumOdd(int n) {
        int result = 0;
        for (int i = 1; i <= n; i += 2) {
            result += i;
        }
        return result;
    }

    public static void main(String[] args) {
        // test your code here
        System.out.println(sumOdd(5));
        System.out.println(sumOdd(10));
    }

}
