question.init:
public class X {

    // write your code here

    public static void main(String[] args) {
        // test your code here
    }

}

question.answer:
public class X {

    // write your code here
    public static int arrayProduct(int[] x) {
        int result = 1;
        for (int i = 0; i < x.length; i++) {
            result *= x[i];
        }
        return result;
    }

    public static void main(String[] args) {
        // test your code here
        System.out.println(arrayProduct(new int[]{10, 9, 8}));
    }

}
