question.init:
public class X {

    // write your code here

    public static void main(String[] args) {
        // test your code here
    }

}

question.answer:
public class X {

    // write your code here
    public static int arrayMax(int[] x, int n, int m){
        int result = x[n];
        for (int i = n; i < m; i++) {
            if (x[i] > result) {
                result = x[i];
            }
        }
        return result;
    }

    public static void main(String[] args) {
        // test your code here
        System.out.println(arrayMax(new int[]{1, 3, 10, 9, 3, 8, 5}, 3, 5));
    }

}
